// Enhanced Route Detection with DCWF Priority
const update = $json;
let routeType = 'unknown';
let data = {};

// Skip bot messages
if (update.message?.from?.is_bot) {
  return { json: { routeType: 'skip', reason: 'Bot message ignored' } };
}

// Check external webhook
if (update.headers && update.headers['x-source'] === 'external-upload') {
  const doc = update.body?.document || {};
  routeType = 'document';
  data = {
    routeType: 'document',
    fileId: doc.fileId,
    fileName: doc.fileName || 'unknown',
    mimeType: doc.mimeType || 'application/octet-stream',
    fileSize: doc.fileSize,
    fileUrl: doc.url,
    fileContent: doc.content,
    chatId: update.body.chatId || 'external',
    userId: update.body.userId || 'external-user',
    username: update.body.username || 'External User',
    messageId: update.body.messageId,
    source: 'external',
    uploadTimestamp: new Date().toISOString()
  };
}
// Handle Telegram messages
else if (update.message) {
  // Check if it's a command
  if (update.message.text?.startsWith('/')) {
    const command = update.message.text.split(' ')[0].toLowerCase();
    if (command === '/dcwf') {
      routeType = 'dcwf_command';
    } else if (command === '/export') {
      routeType = 'export';
    } else if (command === '/stats') {
      routeType = 'stats';
    } else {
      routeType = command.substring(1);
    }
    data = {
      routeType: routeType,
      command: command,
      text: update.message.text,
      chatId: update.message.chat.id,
      userId: update.message.from.id,
      username: update.message.from.username || update.message.from.first_name,
      messageId: update.message.message_id
    };
  }
  // Check if it's a document
  else if (update.message.document) {
    const doc = update.message.document;
    const fileName = doc.file_name?.toLowerCase() || '';

    // Enhanced DCWF detection
    const isDCWFUpload = fileName.includes('dcwf') || 
                        fileName.includes('task') ||
                        fileName.includes('nist') ||
                        fileName.includes('cybersecurity') ||
                        fileName.includes('framework');

    routeType = isDCWFUpload ? 'dcwf_upload' : 'document';
    data = {
      routeType: routeType,
      fileId: doc.file_id,
      fileName: doc.file_name,
      mimeType: doc.mime_type,
      fileSize: doc.file_size,
      chatId: update.message.chat.id,
      userId: update.message.from.id,
      username: update.message.from.username || update.message.from.first_name,
      messageId: update.message.message_id,
      source: 'telegram',
      priority: isDCWFUpload ? 'high' : 'normal',
      uploadTimestamp: new Date().toISOString()
    };
  }
  // Check if it's a URL
  else if (update.message.text) {
    const text = update.message.text;
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const urls = text.match(urlRegex);

    if (urls && urls.length > 0) {
      const url = urls[0];
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        routeType = 'youtube';
      } else if (url.includes('tiktok.com')) {
        routeType = 'tiktok';
      } else {
        routeType = 'url';
      }
      data = {
        routeType: routeType,
        url: url,
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        uploadTimestamp: new Date().toISOString()
      };
    } else {
      // Check for greetings first
      const greetingPatterns = [
        /^(hi|hello|hey|howdy|greetings|good morning|good afternoon|good evening|sup|what's up|yo)$/i,
        /^(hi there|hello there|hey there|good day|howdy partner|what's good|what's happening)$/i,
        /^(hiya|hola|bonjour|guten tag|ciao|salut|namaste|shalom|konnichiwa|hoi|aloha)$/i,
        /^(morning|afternoon|evening|night)$/i,
        /^(üëã|üôã|üôã‚Äç‚ôÇÔ∏è|üôã‚Äç‚ôÄÔ∏è|üëãüèª|üëãüèº|üëãüèΩ|üëãüèæ|üëãüèø)$/,
        /^(üñêÔ∏è|‚úã|ü§ö|ü´±|ü´≤)$/
      ];

      const isGreeting = greetingPatterns.some(pattern => pattern.test(text.trim()));

      // Both greetings and regular messages go to chat route for RAG processing
      routeType = 'chat';
      data = {
        routeType: 'chat',
        message: text,
        query: text,
        text: text,
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        timestamp: new Date().toISOString(),
        isGreeting: isGreeting // Flag to indicate this is a greeting for special handling
      };
    }
  }
}

return { json: data };

// Process DCWF tasks for workflow use
const tasks = $('Load DCWF Tasks').all().map(item => item.json);

console.log(`üéØ Processing ${tasks.length} DCWF tasks`);

// Store in workflow variables
workflow.variables = workflow.variables || {};
workflow.variables.dcwfTaskList = {
  tasks: tasks,
  totalTasks: tasks.length,
  loadedAt: new Date().toISOString(),
  taskMap: tasks.reduce((map, task) => {
    map[task.task_id] = task;
    if (task.nist_sp_id) map[task.nist_sp_id] = task;
    return map;
  }, {})
};

return {
  json: {
    message: `‚úÖ Loaded ${tasks.length} DCWF tasks into workflow memory`,
    totalTasks: tasks.length,
    sampleTasks: tasks.slice(0, 5).map(t => `${t.task_id}: ${t.task_name}`),
    readyForClassification: true
  }
};

// Handle document source and prepare for processing
const data = $json;

if (data.source === 'external') {
  // External document - already has URL or content
  return {
    json: {
      ...data,
      binary: data.fileContent ? {
        data: {
          data: data.fileContent,
          mimeType: data.mimeType,
          fileName: data.fileName
        }
      } : null
    }
