// Process CSV/XLSX data for normalization
const data = $json;
const allRows = [];

Object.keys(data).forEach(sheetName => {
  const sheet = data[sheetName];
  if (Array.isArray(sheet)) {
    sheet.forEach((row, index) => {
      allRows.push({
        ...row,
        _sheet: sheetName,
        _rowIndex: index
      });
    });
  }
});

const headers = allRows.length > 0 ? Object.keys(allRows[0]).filter(k => !k.startsWith('_')) : [];
let textContent = `Spreadsheet Data with ${allRows.length} rows and ${headers.length} columns\n\n`;
textContent += `Headers: ${headers.join(', ')}\n\n`;

if (allRows.length > 100) {
  textContent += `Data Summary:\n`;
  textContent += `- Total rows: ${allRows.length}\n`;
  textContent += `- Sample data (first 10 rows):\n`;
  textContent += JSON.stringify(allRows.slice(0, 10), null, 2);
} else {
  textContent += `Full Data:\n`;
  textContent += JSON.stringify(allRows, null, 2);
}

const metadata = $('Document Source Handler').item.json;

return {
  json: {
    text: textContent,
    content: textContent,
    source_type: metadata.mimeType?.includes('csv') ? 'csv' : 'xlsx',
    filename: metadata.fileName,
    chatId: metadata.chatId,
    userId: metadata.userId,
    username: metadata.username,
    metadata: {
      rowCount: allRows.length,
      columnCount: headers.length,
      headers: headers
    }
  }
};

// Enhanced Content Normalization with explicit error handling
try {
  console.log('Starting content normalization...');

  const sources = [
    { node: 'Extract PDF (Dumpling)', type: 'pdf' },
    { node: 'Extract DOCX (Dumpling)', type: 'docx' },
    { node: 'Process Spreadsheet', type: 'spreadsheet' },
    { node: 'Get YouTube Transcript', type: 'youtube' },
    { node: 'Get TikTok Transcript', type: 'tiktok' }
  ];

  let content = '';
  let sourceType = '';
  let metadata = {};
  let errorMessages = [];

  // Get route data
  let routeData = {};
  try {
    routeData = $('Detect Route Type').item.json;
  } catch (e) {
    console.log('Route data not available');
  }

  // Also check Document Source Handler
  try {
    const docHandler = $('Document Source Handler');
    if (docHandler && docHandler.item && docHandler.item.json) {
      routeData = { ...routeData, ...docHandler.item.json };
    }
  } catch (e) {
    console.log('Document Source Handler data not available');
  }

  // Try each source
  for (const source of sources) {
    try {
      const nodeData = $(source.node).item;
      if (!nodeData || !nodeData.json) continue;

      const data = nodeData.json;
      console.log(`${source.type} response:`, JSON.stringify(data).substring(0, 500));

      // Check for errors
      if (data.error || (data.statusCode && data.statusCode >= 400)) {
        errorMessages.push(`${source.type}: ${data.error?.message || data.message || 'Request failed'}`);
        continue;
      }

      // Handle Dumpling API response
      if (data.results) {
        content = data.results;
        sourceType = source.type;
        console.log(`Found Dumpling content from ${source.type}: ${content.length} characters`);
      }
      // Handle spreadsheet data
      else if (source.type === 'spreadsheet' && (data.text || data.content)) {
        content = data.text || data.content;
        sourceType = data.source_type || 'spreadsheet';
        metadata = data.metadata || {};
      }
      // Other formats
      else {
        const textFields = ['text', 'content', 'transcript', 'extracted_text', 'body', 'output', 'result', 'data'];
        for (const field of textFields) {
          if (data[field] && typeof data[field] === 'string') {
            content = data[field];
            sourceType = source.type;
            break;
          }
        }
      }

      if (content) {
        metadata = {
          filename: routeData.fileName || data.filename || 'unknown',
          source_url: routeData.url || data.url || null,
          chatId: routeData.chatId || null,
          userId: routeData.userId || null,
          username: routeData.username || null,
          extracted_at: new Date().toISOString(),
          uploadTimestamp: routeData.uploadTimestamp || new Date().toISOString(),
          ...metadata,
          ...(data.metadata || {})
        };
        break;
      }
    } catch (e) {
      console.log(`Error processing ${source.type}:`, e.message);
      errorMessages.push(`${source.type}: ${e.message}`);
    }
  }

  // If no content found, provide error
  if (!content) {
    const errorDetail = errorMessages.length > 0 
      ? `Errors encountered:\n${errorMessages.join('\n')}`
      : 'No content could be extracted from any source.';

    throw new Error(`Content extraction failed. ${errorDetail}`);
  }

  // Ensure content is string
  if (typeof content !== 'string') {
    content = JSON.stringify(content);
  }

  return {
    json: {
      content,
      source_type: sourceType,
      filename: metadata.filename,
      source_url: metadata.source_url,
      metadata,
      content_length: content.length,
      processing_timestamp: new Date().toISOString(),
      uploadTimestamp: metadata.uploadTimestamp
    }
  };
} catch (error) {
  return {
    json: {
      content: '',
      source_type: 'error',
      error: true,
      error_message: error.message,
      processing_timestamp: new Date().toISOString(),
      metadata: { chatId: null, userId: null, username: null }
    }
  };
}

// Enhanced Semantic Chunking with DCWF awareness
function semanticChunk(text, maxChunkSize = 1500, overlap = 200) {
  const sentences = text.match(/[^.!?]+[.!?]+/g) || [];
  const chunks = [];
  let currentChunk = '';

  for (const sentence of sentences) {
    if ((currentChunk + sentence).length > maxChunkSize) {
      chunks.push(currentChunk.trim());
      const overlapText = currentChunk.split(' ').slice(-20).join(' ');
      currentChunk = overlapText + ' ' + sentence;
    } else {
      currentChunk += ' ' + sentence;
    }
  }

  if (currentChunk) chunks.push(currentChunk.trim());

  return chunks.map((chunk, index) => ({
    content: chunk,
    chunk_index: index,
    total_chunks: chunks.length,
    metadata: {
      chunking_strategy: 'semantic_with_overlap',
      chunk_size: chunk.length
    }
  }));
}

const data = $json;
const content = data.content || '';

if (!content || content.length === 0) {
  return [{
    json: {
      text: '',
      chunk_index: 0,
      total_chunks: 1,
      chunk_size: 0,
      error: 'No content to chunk',
      ...data
    }
  }];
}

const semanticChunks = semanticChunk(content);

const chunks = semanticChunks.map(chunk => ({
  json: {
    text: chunk.content,
    content: chunk.content,
    chunk_index: chunk.chunk_index,
    total_chunks: chunk.total_chunks,
    chunk_size: chunk.metadata.chunk_size,
    source_type: data.source_type,
    filename: data.filename,
    source_url: data.source_url,
    metadata: { ...data.metadata, ...chunk.metadata },
    original_length: content.length,
    uploadTimestamp: data.uploadTimestamp
  }
}));

console.log(`Created ${chunks.length} semantic chunks`);
return chunks;

