// Enhanced Metadata Enrichment with upload tracking and improved summaries
const chunk = $json;
const timestamp = new Date().toISOString();
const artifactId = `artifact_${Date.now()}_${chunk.chunk_index || 0}_${Math.random().toString(36).substr(2, 9)}`;

// Track upload order for "last document" queries
const uploadOrder = Date.now();
const uploadTimestamp = chunk.uploadTimestamp || timestamp;

// Get AI classification
let aiResponse = '{}';
try {
  const aiNode = $('AI Classification with DCWF');
  if (aiNode && aiNode.item && aiNode.item.json) {
    aiResponse = aiNode.item.json.choices?.[0]?.message?.content || '{}';
  }
} catch (e) {
  console.log('AI Classification not available');
}

let classification = {};
try {
  classification = JSON.parse(aiResponse);
  const validClassifications = ['Replace', 'Augment', 'Remain Human', 'New Task'];
  if (!validClassifications.includes(classification.classification)) {
    classification.classification = 'Unknown';
  }
} catch (error) {
  classification = {
    classification: 'Unknown',
    confidence: 0,
    rationale: 'Classification failed',
    scores: { impact: 0, specificity: 0, credibility: 0 },
    dcwf_tasks: [],
    work_roles: []
  };
}

// Map DCWF tasks if available
const dcwfTaskList = workflow.variables?.dcwfTaskList;
const mappedTasks = [];

if (dcwfTaskList && classification.dcwf_tasks) {
  classification.dcwf_tasks.forEach(taskId => {
    const task = dcwfTaskList.taskMap?.[taskId];
    if (task) {
      mappedTasks.push({
        task_id: task.task_id,
        nist_sp_id: task.nist_sp_id,
        task_name: task.task_name,
        task_description: task.task_description?.substring(0, 200)
      });
    }
  });
}

// Auto-categorization based on content
const contentLower = chunk.text?.toLowerCase() || '';
let category = 'General';

if (contentLower.includes('threat') || contentLower.includes('attack') || contentLower.includes('vulnerability')) {
  category = 'Threat Intelligence';
} else if (contentLower.includes('incident') || contentLower.includes('response') || contentLower.includes('forensic')) {
  category = 'Incident Response';
} else if (contentLower.includes('compliance') || contentLower.includes('policy') || contentLower.includes('governance')) {
  category = 'Governance & Compliance';
} else if (contentLower.includes('security operations') || contentLower.includes('soc') || contentLower.includes('monitoring')) {
  category = 'Security Operations';
} else if (contentLower.includes('identity') || contentLower.includes('access') || contentLower.includes('authentication')) {
  category = 'Identity & Access';
}

// Generate enhanced title
const metadata = chunk.metadata || {};
let title = '';
if (metadata.filename && metadata.filename !== 'unknown') {
  title = metadata.filename
    .replace(/\.(pdf|docx|txt|csv|xlsx)$/i, '')
    .replace(/_/g, ' ')
    .replace(/\b\w/g, l => l.toUpperCase());
} else if (chunk.source_url) {
  // Extract title from URL
  const urlParts = chunk.source_url.split('/');
  title = urlParts[urlParts.length - 1] || 'Web Content';
} else if (chunk.text) {
  const firstLine = chunk.text.split('\n').find(line => line.trim().length > 10);
  title = firstLine ? firstLine.substring(0, 100) : 'Untitled Document';
}

// Generate enhanced summary
let summary = '';

// Add source type context
if (chunk.source_type === 'youtube' || chunk.source_type === 'tiktok') {
  summary += `Video transcript from ${chunk.source_type}. `;
} else if (chunk.source_type === 'csv' || chunk.source_type === 'xlsx') {
  summary += `Spreadsheet data analysis. `;
}

// Add classification context
if (classification.classification !== 'Unknown') {
  summary += `This document outlines how artificial intelligence will ${classification.classification.toLowerCase()} cybersecurity tasks`;
  if (classification.classification === 'Replace') {
    summary += ', indicating full automation potential';
  } else if (classification.classification === 'Augment') {
    summary += ', indicating significant automation potential but emphasizing that human oversight will remain necessary';
  } else if (classification.classification === 'Remain Human') {
    summary += ', indicating tasks will remain primarily human-driven';
  } else if (classification.classification === 'New Task') {
    summary += ', representing new capabilities created by AI advancement';
  }
  summary += `. `;
}

// Add rationale if available
if (classification.rationale && classification.rationale !== 'No rationale provided') {
  summary += classification.rationale + ' ';
}

// Add content preview
const preview = chunk.text?.replace(/\s+/g, ' ').trim().substring(0, 150) || '';
if (preview) {
  summary += `Content preview: "${preview}..."`;
}

// Return enriched metadata
return {
  json: {
    artifact_id: artifactId,
    title: title || 'Untitled Document',
    summary: summary || 'No summary available',
    content: chunk.text || chunk.content || '',
    source_url: chunk.source_url || metadata.source_url || null,
    source_type: chunk.source_type || 'unknown',
    filename: chunk.filename || metadata.filename || 'unknown',
    chunk_index: chunk.chunk_index || 0,
    total_chunks: chunk.total_chunks || 1,
    chunk_size: chunk.chunk_size || 0,
    retrieved_on: timestamp,
    created_at: timestamp,
    upload_timestamp: uploadTimestamp,
    upload_order: uploadOrder,
    user_id: metadata.userId || metadata.user_id || null,
    chat_id: metadata.chatId || metadata.chat_id || null,
    username: metadata.username || null,
    classification: classification.classification || 'Unknown',
    confidence: parseFloat(classification.confidence) || 0,
    rationale: classification.rationale || 'No rationale provided',
    scores: {
      credibility: parseFloat(classification.scores?.credibility) || 0,
      impact: parseFloat(classification.scores?.impact) || 0,
      specificity: parseFloat(classification.scores?.specificity) || 0
    },
    impact_score: parseFloat(classification.scores?.impact) || 0,
    dcwf_task_ids: classification.dcwf_tasks || [],
    mapped_tasks: mappedTasks,
    work_roles: classification.work_roles || [],
    category: category,
    tags: [category.toLowerCase().replace(/[^a-z0-9]/g, '_')],
    evidence_strength: classification.confidence > 0.8 ? 'HIGH' : classification.confidence > 0.5 ? 'MEDIUM' : 'LOW',
    metadata: {
      ...metadata,
      artifact_id: artifactId,
      processing_timestamp: timestamp,
      upload_timestamp: uploadTimestamp,
      upload_order: uploadOrder,
      chunk_metadata: chunk.metadata || {},
      classification_metadata: classification,
      auto_categorization: { category: category },
      dcwf_mapping: mappedTasks,
      enhanced_summary: summary
    }
  }
};

