// Enhanced Chat Input with Query Type Detection
const input = $json;
const userMessage = input.message || input.query || input.text || '';

if (!userMessage) {
  throw new Error('No message provided');
}

// Query type detection patterns
const queryTypes = {
  lastDocument: /last\s+(document|file|upload|doc)|recent\s+(document|file|upload)|just\s+(uploaded|sent)|what\s+did\s+i\s+(upload|send)/i,
  evidenceQuery: /evidence.*support.*classification|what\s+evidence.*(?:Replace|Augment|Remain Human|New Task)|proof.*classification/i,
  dcwfTaskQuery: /DCWF.*task|task.*[A-Z]{2,4}-\d{3}|cybersecurity\s+task|what.*tasks?.*relate/i,
  classificationQuery: /how.*classified|why.*(?:Replace|Augment|Remain Human|New Task)|explain.*classification/i,
  documentSearch: /find.*document|search.*(?:about|for)|show.*documents?.*about/i,
  statsQuery: /how\s+many|statistics|summary.*all|overview/i
};

// Detect query type
let detectedQueryType = 'general';
let queryContext = {};

for (const [type, pattern] of Object.entries(queryTypes)) {
  if (pattern.test(userMessage)) {
    detectedQueryType = type;

    // Extract specific context based on query type
    if (type === 'evidenceQuery') {
      // Try to extract classification mentioned
      const classificationMatch = userMessage.match(/(?:Replace|Augment|Remain Human|New Task)/i);
      if (classificationMatch) {
        queryContext.targetClassification = classificationMatch[0];
      }

      // Try to extract DCWF task mentioned
      const taskMatch = userMessage.match(/[A-Z]{2,4}-\d{3}/g);
      if (taskMatch) {
        queryContext.targetTasks = taskMatch;
      }
    } else if (type === 'dcwfTaskQuery') {
      // Extract specific task IDs if mentioned
      const taskMatch = userMessage.match(/[A-Z]{2,4}-\d{3}/g);
      if (taskMatch) {
        queryContext.specificTasks = taskMatch;
      }
    }
    break;
  }
}

// Enhanced context building
const chatContext = {
  userMessage: userMessage,
  chatId: input.chatId,
  userId: input.userId,
  username: input.username,
  messageId: input.messageId,
  timestamp: input.timestamp || new Date().toISOString(),

  // Greeting detection
  isGreeting: input.isGreeting || false,

  // Query analysis - override to 'greeting' if greeting detected
  queryType: input.isGreeting ? 'greeting' : detectedQueryType,
  queryContext: queryContext,

  // Memory integration flags
  requiresMemoryRetrieval: true,
  requiresDocumentSearch: true,
  requiresDCWFMapping: detectedQueryType === 'dcwfTaskQuery' || detectedQueryType === 'evidenceQuery',

  // Enhanced flags
  isLastDocumentQuery: detectedQueryType === 'lastDocument',
  isEvidenceQuery: detectedQueryType === 'evidenceQuery',
  isDCWFQuery: detectedQueryType === 'dcwfTaskQuery',
  isClassificationQuery: detectedQueryType === 'classificationQuery',

  // Context enhancement
  dcwfTaskMentioned: /[A-Z]{2,4}-\d{3}|DCWF|task\s+\d+/i.test(userMessage),
  evidenceRequest: userMessage.includes('proof') || userMessage.includes('evidence') || userMessage.includes('support'),

  // DCWF context
  dcwfTasksAvailable: workflow.variables?.dcwfTaskList?.totalTasks || 0
};

return { json: chatContext };

// Enhanced Document Search based on query type
const chatData = $('Prepare Enhanced Chat Input').item.json;
const userId = chatData.userId;
const queryType = chatData.queryType;
const queryContext = chatData.queryContext;

// Build search parameters based on query type
let searchParams = {
  user_id: userId,
  limit: 10
};

if (queryType === 'lastDocument') {
  // For last document queries, we'll use the fallback search sorted by upload time
  searchParams.sort_field = 'upload_timestamp';
  searchParams.sort_direction = 'desc';
  searchParams.limit = 1;
} else if (queryType === 'evidenceQuery' && queryContext.targetClassification) {
  // Search for documents with specific classification
  searchParams.classification = queryContext.targetClassification;
  searchParams.limit = 20;
} else if (queryType === 'dcwfTaskQuery' && queryContext.specificTasks) {
  // Search for documents mentioning specific DCWF tasks
  searchParams.dcwf_tasks = queryContext.specificTasks;
  searchParams.limit = 15;
} else {
  // General semantic search
  searchParams.query_text = chatData.userMessage;
  searchParams.match_threshold = 0.7;
}

return {
  json: searchParams
};

// Enhanced Multi-Agent Context Builder with Query-Specific Logic
let chatData, memoryHistory = [], documentResults = [];

// Get chat input with query analysis
try {
  chatData = $('Prepare Enhanced Chat Input').item.json;
} catch (e) {
  throw new Error('Chat input data not available');
}

// Get embedded memory
try {
  memoryHistory = $('Get Chat Memory').all().map(item => item.json) || [];
} catch (e) {
  console.log('No embedded memory available');
}

// Get document search results
try {
  // Try RPC function first
  const vectorSearch = $('Vector Search Documents');
  if (vectorSearch && vectorSearch.all) {
    documentResults = vectorSearch.all().map(item => item.json) || [];
  }
} catch (e) {
  // Fallback to enhanced search
  try {
    const fallbackSearch = $('Enhanced Document Search');
    if (fallbackSearch && fallbackSearch.all) {
      documentResults = fallbackSearch.all().map(item => item.json) || [];
    }
  } catch (e2) {
    console.log('No document search results available');
  }
}

// Build query-specific context
const dcwfTaskList = workflow.variables?.dcwfTaskList;
const dcwfTasks = dcwfTaskList?.tasks || [];

// Format context based on query type
let enhancedContext = '';
let responseInstructions = '';

if (chatData.queryType === 'lastDocument') {
  // Focus on the most recent document
  if (documentResults.length > 0) {
    const lastDoc = documentResults[0];
    enhancedContext = `\n\nüìÑ **Last Document Uploaded:**\n`;
    enhancedContext += `**Title:** "${lastDoc.title}"\n`;
    enhancedContext += `**File:** ${lastDoc.filename}\n`;
    enhancedContext += `**Classification:** ${lastDoc.classification} (${(lastDoc.confidence * 100).toFixed(0)}% confidence)\n`;
    enhancedContext += `**Summary:** ${lastDoc.summary}\n`;

    if (lastDoc.dcwf_task_ids?.length > 0) {
      enhancedContext += `\n**Associated DCWF Tasks:**\n`;
      lastDoc.dcwf_task_ids.forEach(taskId => {
        const task = dcwfTaskList?.taskMap?.[taskId];
        if (task) {
          enhancedContext += `- ${taskId} (${task.nist_sp_id}): ${task.task_name}\n`;
        }
      });
    }

    if (lastDoc.tags?.length > 0) {
      enhancedContext += `\n**Tags:** ${lastDoc.tags.join(', ')}\n`;
    }

    if (lastDoc.source_url) {
      enhancedContext += `**URL:** ${lastDoc.source_url}\n`;
    }

    responseInstructions = 'Provide a detailed response about the last document uploaded, following the exact format shown in the context.';
  } else {
    enhancedContext = '\n\nNo documents have been uploaded yet.';
    responseInstructions = 'Inform the user that no documents have been uploaded.';
  }

} else if (chatData.queryType === 'evidenceQuery') {
  // Aggregate evidence for specific classification
  const targetClassification = chatData.queryContext.targetClassification;
  const relevantDocs = documentResults.filter(doc => 
    !targetClassification || doc.classification === targetClassification
  );

  if (relevantDocs.length > 0) {
    enhancedContext = `\n\nüîç **Evidence Supporting Classification${targetClassification ? ' "' + targetClassification + '"' : ''}:**\n\n`;

    relevantDocs.forEach((doc, index) => {
      enhancedContext += `${index + 1}. **Document:** "${doc.title}"\n`;
      enhancedContext += `   - **Classification:** ${doc.classification} (${(doc.confidence * 100).toFixed(0)}% confidence)\n`;
      enhancedContext += `   - **Rationale:** ${doc.rationale}\n`;
      enhancedContext += `   - **Credibility:** ${(doc.scores?.credibility * 100).toFixed(0)}%, `;
      enhancedContext += `**Impact:** ${(doc.scores?.impact * 100).toFixed(0)}%, `;
      enhancedContext += `**Specificity:** ${(doc.scores?.specificity * 100).toFixed(0)}%\n`;
      if (doc.source_url) {
        enhancedContext += `   - **URL:** ${doc.source_url}\n`;
      }
      enhancedContext += `\n`;
    });

    responseInstructions = 'List all the evidence in a numbered format, showing documents with their classifications, confidence scores, rationale, and credibility metrics.';
  } else {
    enhancedContext = `\n\nNo evidence found${targetClassification ? ' for classification "' + targetClassification + '"' : ''}.`;
    responseInstructions = 'Inform the user that no supporting evidence was found.';
  }

} else if (chatData.queryType === 'dcwfTaskQuery') {
  // Focus on DCWF task information
  const specificTasks = chatData.queryContext.specificTasks || [];
  let relevantTasks = [];

  if (specificTasks.length > 0) {
    // Find specific tasks mentioned
    relevantTasks = specificTasks.map(taskId => dcwfTaskList?.taskMap?.[taskId]).filter(Boolean);
  } else {
    // Find tasks mentioned in user message
    const messageWords = chatData.userMessage.toLowerCase().split(/\s+/);
    dcwfTasks.forEach(task => {
      const taskWords = `${task.task_id} ${task.nist_sp_id} ${task.task_name} ${task.task_description}`.toLowerCase();
      for (const word of messageWords) {
        if (word.length > 3 && taskWords.includes(word)) {
          relevantTasks.push(task);
          break;
        }
      }
    });
  }

  if (relevantTasks.length > 0) {
    enhancedContext = `\n\nüéØ **DCWF Tasks Analysis:**\n\n`;
    relevantTasks.forEach(task => {
      enhancedContext += `**${task.task_id}** (${task.nist_sp_id}): ${task.task_name}\n`;
      enhancedContext += `üìù ${task.task_description}\n\n`;

      // Find documents related to this task
      const relatedDocs = documentResults.filter(doc => 
        doc.dcwf_task_ids?.includes(task.task_id)
      );

      if (relatedDocs.length > 0) {
        enhancedContext += `**Related Documents:**\n`;
        relatedDocs.forEach(doc => {
          enhancedContext += `- "${doc.title}" - ${doc.classification} (${(doc.confidence * 100).toFixed(0)}%)\n`;
        });
        enhancedContext += `\n`;
      }
    });

    responseInstructions = 'Provide detailed information about the DCWF tasks, including their descriptions and any related documents.';
  } else {
    enhancedContext = `\n\nüìä **DCWF Knowledge Base:** ${dcwfTasks.length} cybersecurity tasks available.`;
    responseInstructions = 'Explain that DCWF tasks are available but no specific tasks were found matching the query.';
  }

} else {
  // General query - standard context
  const memoryContext = memoryHistory
    .slice(0, 10)
    .map(msg => {
      const metadata = typeof msg.metadata === 'string' ? JSON.parse(msg.metadata) : msg.metadata || {};
      const evidence = metadata.evidence ? ` [Evidence: ${JSON.stringify(metadata.evidence)}]` : '';
      return `${msg.role === 'user' ? 'User' : 'Assistant'}: ${msg.content}${evidence}`;
    })
    .join('\n\n');

  let documentContext = 'No relevant documents found.';
  if (documentResults.length > 0) {
    documentContext = documentResults.slice(0, 5)
      .map(doc => {
        const title = doc.title || 'Untitled';
        const classification = doc.classification || 'Unknown';
        const confidence = doc.confidence ? `(${(doc.confidence * 100).toFixed(0)}% confidence)` : '';
        const content = (doc.content || '').substring(0, 400);
        const dcwfTasks = doc.dcwf_task_ids?.length ? `\nüéØ DCWF Tasks: ${doc.dcwf_task_ids.join(', ')}` : '';
        const evidence = doc.evidence_strength ? `\nüîç Evidence Strength: ${doc.evidence_strength}` : '';
        const url = doc.source_url ? `\nüîó URL: ${doc.source_url}` : '';

        return `üìÑ **${title}**\nüè∑Ô∏è Classification: ${classification} ${confidence}${dcwfTasks}${evidence}${url}\nüìù Content: ${content}...\n`;
      })
      .join('\n---\n');
  }

  enhancedContext = `\n\n**Knowledge Base Context:**\n${documentContext}\n\n**Recent Conversation:**\n${memoryContext || 'No previous conversation.'}`;

  responseInstructions = 'Provide a helpful response based on the available context, citing specific documents when relevant.';
}

// Build enhanced system message
const systemMessage = `You are an advanced AI assistant for Project Horizon, specializing in cybersecurity workforce analysis using the DCWF (Defense Cybersecurity Workforce Framework).

