// Enhanced Route Detection with Bot Message Filtering
const update = $json;
let routeType = 'unknown';
let data = {};

// IMPORTANT: Skip bot messages to prevent loops
if (update.message && update.message.from && update.message.from.is_bot) {
  console.log('Skipping bot message to prevent loop');
  return {
    json: {
      routeType: 'skip',
      reason: 'Bot message ignored to prevent loop'
    }
  };
}

// Check if it's from external webhook
if (update.headers && update.headers['x-source'] === 'external-upload') {
  // Handle external document upload
  if (update.body && update.body.document) {
    const doc = update.body.document;
    routeType = 'document';
    data = {
      routeType: 'document',
      fileId: doc.fileId || null,
      fileName: doc.fileName || doc.name || 'unknown',
      mimeType: doc.mimeType || 'application/octet-stream',
      fileSize: doc.fileSize || null,
      fileUrl: doc.url || null,
      fileContent: doc.content || null,
      chatId: update.body.chatId || 'external',
      userId: update.body.userId || 'external-user',
      username: update.body.username || 'External User',
      messageId: update.body.messageId || null,
      source: 'external'
    };
  }
}
// Handle Telegram messages
else if (update.message) {
  // Check if it's a command
  if (update.message.text?.startsWith('/')) {
    const command = update.message.text.split(' ')[0].toLowerCase();

    if (command === '/export') {
      routeType = 'export';
      data = {
        routeType: 'export',
        command: 'export',
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        filters: update.message.text.substring(7).trim()
      };
    } else if (command === '/stats') {
      routeType = 'stats';
      data = {
        routeType: 'stats',
        command: 'stats',
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id
      };
    }
  }
  // Check if it's a document upload
  else if (update.message.document) {
    const doc = update.message.document;
    routeType = 'document';
    data = {
      routeType: 'document',
      fileId: doc.file_id,
      fileName: doc.file_name || 'unknown',
      mimeType: doc.mime_type || 'application/octet-stream',
      fileSize: doc.file_size,
      chatId: update.message.chat.id,
      userId: update.message.from.id,
      username: update.message.from.username || update.message.from.first_name,
      messageId: update.message.message_id,
      source: 'telegram'
    };
  } 
  // Check if it's a text message with URL
  else if (update.message.text) {
    const text = update.message.text;
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const urls = text.match(urlRegex);

    if (urls && urls.length > 0) {
      const url = urls[0];

      // Check if it's YouTube or TikTok
      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        routeType = 'youtube';
        data = {
          routeType: 'youtube',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id
        };
      } else if (url.includes('tiktok.com')) {
        routeType = 'tiktok';
        data = {
          routeType: 'tiktok',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id
        };
      } else {
        // Generic URL - could be RSS or article
        routeType = 'url';
        data = {
          routeType: 'url',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id
        };
      }
    } else {
      // Regular chat message
      routeType = 'chat';
      data = {
        routeType: 'chat',
        message: text,
        query: text,
        text: text,
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        timestamp: new Date().toISOString()
      };
    }
  }
}

return {
  json: data
};

// Handle external document or download Telegram file
const data = $json;

if (data.source === 'external') {
  // External document - already has URL or content
  return {
    json: {
      ...data,
      binary: data.fileContent ? {
        data: {
          data: data.fileContent,
          mimeType: data.mimeType,
          fileName: data.fileName
        }
      } : null
    }
  };
} else {
  // Telegram document - needs download
  return {
    json: data
  };
}

// Prepare message for AI processing
const input = $json;
const userMessage = input.message || input.query || input.text || '';

if (!userMessage) {
  throw new Error('No message provided');
}

return {
  json: {
    userMessage: userMessage,
    chatId: input.chatId,
    userId: input.userId,
    username: input.username,
    messageId: input.messageId,
    timestamp: input.timestamp || new Date().toISOString()
  }
};

// Direct Supabase query for vector search
const userMessage = $('Prepare Chat Input').item.json.userMessage;
const userId = $('Prepare Chat Input').item.json.userId;

// Simple similarity search query - no RPC function needed
return {
  json: {
    query_text: userMessage,
    user_id: userId,
    limit: 10
  }
};

// Build context from chat history and vector search results
let chatData, history = [], searchResults = [];

// Safely get chat data
try {
  chatData = $('Prepare Chat Input').item.json;
} catch (e) {
  throw new Error('Chat input data not available');
}

// Safely get chat history
try {
  history = $('Get Chat History').all() || [];
} catch (e) {
  console.log('No chat history available');
}

// Safely get search results - now from Supabase documents table
try {
  const searchNode = $('Vector Search Documents');
  if (searchNode && searchNode.all) {
    searchResults = searchNode.all().map(item => item.json) || [];
  }
} catch (e) {
  console.log('No search results available');
}

// Format chat history
const formattedHistory = history
  .reverse()
  .map(msg => `${msg.json.role === 'user' ? 'User' : 'Assistant'}: ${msg.json.content}`)
  .join('\n\n');

// Format search results from documents table
let relevantDocs = 'No relevant documents found.';
if (searchResults.length > 0) {
  relevantDocs = searchResults.slice(0, 5)
    .map(doc => {
      const title = doc.title || 'Untitled';
      const category = doc.category || 'General';
      const content = (doc.content || '').substring(0, 500);
      const filename = doc.filename || 'Unknown file';
      const classification = doc.classification || 'Unknown';
      return `Document: ${title}\nFile: ${filename}\nCategory: ${category}\nClassification: ${classification}\nContent: ${content}...\n`;
    })
    .join('\n---\n');
}

// Build the system message
const systemMessage = `You are a helpful AI assistant for Project Horizon. You help users find and understand information about AI's impact on cybersecurity tasks.

Relevant documents from the knowledge base:
${relevantDocs}

Recent conversation history:
${formattedHistory || 'No previous messages.'}

IMPORTANT: When asked about what document was just sent or recently uploaded, refer to the most recent document in the relevant documents section above.`;

return {
  json: {
    systemMessage: systemMessage,
    userMessage: chatData.userMessage,
    chatId: chatData.chatId,
    userId: chatData.userId,
    username: chatData.username,
    messageId: chatData.messageId,
    documentsFound: searchResults.length,
    historyCount: history.length
  }
};

// Process AI response
const aiResponse = $json;
const contextData = $('Build AI Context').item.json;

// Extract the AI's message
const aiMessage = aiResponse.choices?.[0]?.message?.content || 'Sorry, I could not generate a response.';

return {
  json: {
    output: aiMessage,
    userData: {
      chatId: contextData.chatId,
      userId: contextData.userId,
      username: contextData.username,
      messageId: contextData.messageId
    },
    input: contextData.userMessage,
    documentsUsed: contextData.documentsFound,
    model: aiResponse.model || 'gpt-4o-mini',
    timestamp: new Date().toISOString()
  }
};

