// Process CSV data
const csvData = $json;
const allRows = [];

Object.keys(csvData).forEach(sheetName => {
  const sheet = csvData[sheetName];
  if (Array.isArray(sheet)) {
    sheet.forEach((row, index) => {
      allRows.push({
        ...row,
        _sheet: sheetName,
        _rowIndex: index
      });
    });
  }
});

const headers = allRows.length > 0 ? Object.keys(allRows[0]).filter(k => !k.startsWith('_')) : [];
let textContent = `CSV Data with ${allRows.length} rows and ${headers.length} columns\n\n`;
textContent += `Headers: ${headers.join(', ')}\n\n`;

if (allRows.length > 100) {
  textContent += `Data Summary:\n`;
  textContent += `- Total rows: ${allRows.length}\n`;
  textContent += `- Sample data (first 10 rows):\n`;
  textContent += JSON.stringify(allRows.slice(0, 10), null, 2);
} else {
  textContent += `Full Data:\n`;
  textContent += JSON.stringify(allRows, null, 2);
}

const metadata = $('Document Source Handler').item.json;

return {
  json: {
    text: textContent,
    content: textContent,
    source_type: 'csv',
    filename: metadata.fileName,
    chatId: metadata.chatId,
    userId: metadata.userId,
    username: metadata.username,
    metadata: {
      rowCount: allRows.length,
      columnCount: headers.length,
      headers: headers
    }
  }
};

// Enhanced Normalize Content Node with comprehensive metadata extraction
try {
  console.log("Starting content normalization...");

  const sources = [
    { node: 'Extract PDF (Dumpling)', type: 'pdf' },
    { node: 'Extract DOCX (Dumpling)', type: 'docx' },
    { node: 'Process CSV', type: 'csv' },
    { node: 'Get YouTube Transcript', type: 'youtube' },
    { node: 'Get TikTok Transcript', type: 'tiktok' },
    { node: 'Extract Web Content', type: 'article' },
    { node: 'Process RSS Items', type: 'rss' }
  ];

  let content = '';
  let sourceType = '';
  let metadata = {};
  let errorMessages = [];

  // Get route data safely
  let routeData = {
    chatId: null,
    userId: null,
    username: null,
    fileName: null,
    url: null
  };

  try {
    const routeNode = $('Detect Route Type');
    if (routeNode && routeNode.item && routeNode.item.json) {
      routeData = routeNode.item.json;
    }
  } catch (e) {
    console.log("Route data not available - using defaults");
  }

  // Also check Document Source Handler for metadata
  try {
    const docHandler = $('Document Source Handler');
    if (docHandler && docHandler.item && docHandler.item.json) {
      const handlerData = docHandler.item.json;
      routeData = {
        ...routeData,
        ...handlerData
      };
    }
  } catch (e) {
    console.log("Document Source Handler data not available");
  }

  for (const source of sources) {
    try {
      let nodeData = null;

      try {
        const node = $(source.node);
        if (node && node.item) {
          nodeData = node.item;
        }
      } catch (e) {
        console.log(`Node ${source.node} not found or has no data`);
        continue;
      }

      if (!nodeData || !nodeData.json) {
        console.log(`No data from ${source.node}`);
        continue;
      }

      const data = nodeData.json;
      console.log(`${source.type} response:`, JSON.stringify(data).substring(0, 500));

      // Check for HTTP error responses
      if (data.error || (data.statusCode && data.statusCode >= 400)) {
        errorMessages.push(`${source.type}: ${data.error?.message || data.message || 'Request failed'}`);
        console.error(`Error from ${source.type}:`, data.error || data.message);
        continue;
      }

      // Handle Dumpling API response format
      if (data.results) {
        content = data.results;
        sourceType = source.type;
        console.log(`Found Dumpling content from ${source.type}: ${content.length} characters`);

        // Ensure DOCX is properly detected
        if (source.node === 'Extract DOCX (Dumpling)') {
          sourceType = 'docx';
          console.log('Confirmed source type as DOCX');
        }
      }
      // Handle Process CSV output
      else if (source.type === 'csv' && (data.text || data.content)) {
        content = data.text || data.content;
        sourceType = 'csv';
        console.log(`Found CSV content: ${content.length} characters`);
      }
      // Check for other standard formats
      else {
        const textFields = ['text', 'content', 'transcript', 'extracted_text', 'body', 'output', 'result', 'data'];
        for (const field of textFields) {
          if (data[field] && typeof data[field] === 'string') {
            content = data[field];
            sourceType = source.type;
            console.log(`Found content in '${field}' field`);
            break;
          }
        }
      }

      if (content) {
        // Build metadata with safe access
        metadata = {
          filename: routeData.fileName || data.filename || 'unknown',
          source_url: routeData.url || data.url || null,
          chatId: routeData.chatId || null,
          userId: routeData.userId || null,
          username: routeData.username || null,
          extracted_at: new Date().toISOString(),
          pages: data.pages || null,
          videoDuration: data.videoDuration || null,
          creditUsage: data.creditUsage || null,
          ...(data.metadata || {})
        };
        break;
      }
    } catch (e) {
      console.log(`Error processing ${source.type}:`, e.message);
      errorMessages.push(`${source.type}: ${e.message}`);
    }
  }

  // If no content found, provide detailed error
  if (!content) {
    const errorDetail = errorMessages.length > 0 
      ? `Errors encountered:\n${errorMessages.join('\n')}`
      : 'No content could be extracted from any source.';

    throw new Error(`Content extraction failed. ${errorDetail}\n\nPlease check:\n1. API keys are valid\n2. File/URL is accessible\n3. File size limits (30MB for docs, 2GB for videos)\n4. API credits are available`);
  }

  // Ensure content is string
  if (typeof content !== 'string') {
    content = JSON.stringify(content);
  }

  // Double-check source type based on file extension
  if (sourceType === 'error' || !sourceType) {
    if (routeData.fileName) {
      const fileName = routeData.fileName.toLowerCase();
      if (fileName.endsWith('.docx')) {
        sourceType = 'docx';
      } else if (fileName.endsWith('.pdf')) {
        sourceType = 'pdf';
      } else if (fileName.endsWith('.csv')) {
        sourceType = 'csv';
      }
      console.log(`Set source type from filename: ${sourceType}`);
    }
  }

  return {
    json: {
      content,
      source_type: sourceType,
      filename: metadata.filename,
      source_url: metadata.source_url,
      metadata,
      content_length: content.length,
      processing_timestamp: new Date().toISOString()
    }
  };
} catch (error) {
  return {
    json: {
      content: '',
      source_type: 'error',
      error: true,
      error_message: error.message,
      processing_timestamp: new Date().toISOString(),
      metadata: {
        chatId: null,
        userId: null,
        username: null
      }
    }
  };
}

// Enhanced Semantic Chunking with Overlap
function semanticChunk(text, maxChunkSize = 1500, overlap = 200) {
  const sentences = text.match(/[^.!?]+[.!?]+/g) || [];
  const chunks = [];
  let currentChunk = '';

  for (const sentence of sentences) {
    if ((currentChunk + sentence).length > maxChunkSize) {
      chunks.push(currentChunk.trim());
      // Include overlap from previous chunk
      const overlapText = currentChunk.split(' ').slice(-20).join(' ');
      currentChunk = overlapText + ' ' + sentence;
    } else {
      currentChunk += ' ' + sentence;
    }
  }

  if (currentChunk) chunks.push(currentChunk.trim());

  return chunks.map((chunk, index) => ({
    content: chunk,
    chunk_index: index,
    total_chunks: chunks.length,
    metadata: {
      chunking_strategy: 'semantic_with_overlap',
      chunk_size: chunk.length
    }
  }));
}

// Get the input data
const data = $json;
console.log("Input data keys:", Object.keys(data));
console.log("Source type received:", data.source_type);
console.log("Content length received:", data.content?.length || 0);

// Extract the content
const content = data.content || '';

console.log(`Starting chunking: ${content.length} characters`);

// If content is empty, return error
if (!content || content.length === 0) {
  console.error("ERROR: No content to chunk!");
  return [{
    json: {
      text: '',
      chunk_index: 0,
      total_chunks: 1,
      chunk_size: 0,
      error: 'No content to chunk',
      ...data
    }
  }];
}

// Use semantic chunking
const semanticChunks = semanticChunk(content);

const chunks = semanticChunks.map(chunk => ({
  json: {
    text: chunk.content,
    content: chunk.content,
    chunk_index: chunk.chunk_index,
    total_chunks: chunk.total_chunks,
    chunk_size: chunk.metadata.chunk_size,
    source_type: data.source_type,
    filename: data.filename,
    source_url: data.source_url,
    metadata: { ...data.metadata, ...chunk.metadata },
    original_length: content.length
  }
}));

console.log(`Created ${chunks.length} semantic chunks`);
console.log("First chunk preview:", chunks[0]?.json?.text?.substring(0, 100));

return chunks;

