// Enhanced Enrich Metadata with Complete Field Mapping
const chunk = $json;
const timestamp = new Date().toISOString();
const artifactId = `artifact_${Date.now()}_${chunk.chunk_index || 0}_${Math.random().toString(36).substr(2, 9)}`;

// Get AI classification
let aiResponse = '{}';
try {
  const aiNode = $('AI Classification');
  if (aiNode && aiNode.item && aiNode.item.json) {
    aiResponse = aiNode.item.json.choices?.[0]?.message?.content || '{}';
  }
} catch (e) {
  console.log("AI Classification not available");
}

let classification = {};
try {
  classification = JSON.parse(aiResponse);
  const validClassifications = ['Replace', 'Augment', 'Remain Human', 'New Task'];
  if (!validClassifications.includes(classification.classification)) {
    classification.classification = 'Unknown';
  }
} catch (error) {
  classification = {
    classification: 'Unknown',
    confidence: 0,
    rationale: 'Classification failed',
    scores: { impact: 0, specificity: 0, credibility: 0 },
    dcwf_tasks: [],
    work_roles: []
  };
}

// Get metadata and content
const metadata = chunk.metadata || {};
const contentText = chunk.text || chunk.content || '';

// Generate title from filename or content
let title = '';
if (metadata.filename && metadata.filename !== 'unknown') {
  title = metadata.filename
    .replace(/\.(pdf|docx|txt|csv)$/i, '')
    .replace(/_/g, ' ')
    .replace(/\b\w/g, l => l.toUpperCase());
} else if (contentText) {
  const firstLine = contentText.split('\n').find(line => line.trim().length > 10);
  title = firstLine ? firstLine.substring(0, 100) : 'Untitled Document';
}

// Auto-categorization based on content
let category = 'General';
const contentLower = contentText.toLowerCase();

if (contentLower.includes('threat') || contentLower.includes('attack') || contentLower.includes('vulnerability')) {
  category = 'Threat Intelligence';
} else if (contentLower.includes('incident') || contentLower.includes('response') || contentLower.includes('forensic')) {
  category = 'Incident Response';
} else if (contentLower.includes('compliance') || contentLower.includes('policy') || contentLower.includes('governance')) {
  category = 'Governance & Compliance';
} else if (contentLower.includes('security operations') || contentLower.includes('soc') || contentLower.includes('monitoring')) {
  category = 'Security Operations';
} else if (contentLower.includes('identity') || contentLower.includes('access') || contentLower.includes('authentication')) {
  category = 'Identity & Access';
}

// Generate auto-tags
const tags = [];
const keywords = ['ai', 'machine learning', 'automation', 'threat', 'vulnerability', 'incident', 'compliance', 'cloud', 'zero trust', 'ransomware', 'phishing', 'malware', 'encryption', 'firewall', 'siem', 'soar'];
keywords.forEach(keyword => {
  if (contentLower.includes(keyword)) {
    tags.push(keyword);
  }
});

// Generate summary (comprehensive)
let summary = '';
if (classification.classification && classification.classification !== 'Unknown') {
  summary += `Classification: ${classification.classification} (${(classification.confidence * 100).toFixed(0)}% confidence). `;
  summary += `${classification.rationale || 'Analysis pending'}. `;
}

// Add content preview to summary
if (contentText.length > 50) {
  const preview = contentText
    .replace(/\s+/g, ' ')
    .trim()
    .substring(0, 200);
  summary += `Content preview: "${preview}..."`;
} else {
  summary += contentText;
}

// Ensure all required fields are properly mapped
return {
  json: {
    // Core identification
    artifact_id: artifactId,

    // Content fields
    title: title || 'Untitled Document',
    summary: summary || 'No summary available',
    content: contentText,

    // Source information
    source_url: chunk.source_url || metadata.source_url || null,
    source_type: chunk.source_type || 'unknown',
    filename: chunk.filename || metadata.filename || 'unknown',

    // Chunk information
    chunk_index: chunk.chunk_index || 0,
    total_chunks: chunk.total_chunks || 1,
    chunk_size: chunk.chunk_size || contentText.length,

    // Timestamps
    retrieved_on: timestamp,
    created_at: timestamp,

    // User information
    user_id: metadata.userId || metadata.user_id || null,
    chat_id: metadata.chatId || metadata.chat_id || null,
    username: metadata.username || null,

    // AI Classification fields
    classification: classification.classification || 'Unknown',
    confidence: parseFloat(classification.confidence) || 0,
    rationale: classification.rationale || 'No rationale provided',

    // Scores
    scores: {
      credibility: parseFloat(classification.scores?.credibility) || 0,
      recency: timestamp,
      impact: parseFloat(classification.scores?.impact) || 0,
      specificity: parseFloat(classification.scores?.specificity) || 0
    },
    impact_score: parseFloat(classification.scores?.impact) || 0,

    // DCWF fields
    dcwf_task_ids: Array.isArray(classification.dcwf_tasks) ? classification.dcwf_tasks : [],
    work_roles: Array.isArray(classification.work_roles) ? classification.work_roles : [],

    // Categorization
    category: category,
    tags: tags.length > 0 ? tags : ['general'],

    // Complete metadata object
    metadata: {
      ...metadata,
      artifact_id: artifactId,
      processing_timestamp: timestamp,
      chunk_metadata: chunk.metadata || {},
      classification_metadata: classification,
      auto_categorization: {
        category: category,
        tags: tags
      }
    }
  }
};

