// Enhanced Route Detection with Extensive Debugging
const update = $json;
let routeType = 'unknown';
let data = {};

console.log('=== ROUTE DETECTION DEBUG ===');
console.log('Full update object:', JSON.stringify(update, null, 2));

// IMPORTANT: Skip bot messages to prevent loops
if (update.message && update.message.from && update.message.from.is_bot) {
  console.log('SKIPPING: Bot message detected');
  return {
    json: {
      routeType: 'skip',
      reason: 'Bot message ignored to prevent loop',
      debug: 'Bot message filtered out'
    }
  };
}

// Handle Telegram messages
if (update.message) {
  console.log('Message found:', update.message);

  // Check for commands first
  if (update.message.text && update.message.text.startsWith('/')) {
    const command = update.message.text.split(' ')[0].toLowerCase();
    console.log('Command detected:', command);

    if (command === '/stats') {
      routeType = 'stats';
      data = {
        routeType: 'stats',
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        debug: 'Stats command route'
      };
    } else if (command === '/export') {
      routeType = 'export';
      data = {
        routeType: 'export',
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        debug: 'Export command route'
      };
    }
  }
  // Check for document attachment
  else if (update.message.document) {
    const doc = update.message.document;
    console.log('Document detected:', doc);
    routeType = 'document';
    data = {
      routeType: 'document',
      fileId: doc.file_id,
      fileName: doc.file_name || 'unknown',
      mimeType: doc.mime_type || 'application/octet-stream',
      fileSize: doc.file_size,
      chatId: update.message.chat.id,
      userId: update.message.from.id,
      username: update.message.from.username || update.message.from.first_name,
      messageId: update.message.message_id,
      source: 'telegram',
      debug: 'Document upload route'
    };
  } 
  // Check for text messages
  else if (update.message.text) {
    const text = update.message.text;
    console.log('Text message detected:', text);

    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const urls = text.match(urlRegex);

    if (urls && urls.length > 0) {
      const url = urls[0];
      console.log('URL detected in text:', url);

      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        routeType = 'youtube';
        data = {
          routeType: 'youtube',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id,
          debug: 'YouTube URL route'
        };
      } else if (url.includes('tiktok.com')) {
        routeType = 'tiktok';
        data = {
          routeType: 'tiktok',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id,
          debug: 'TikTok URL route'
        };
      } else {
        routeType = 'url';
        data = {
          routeType: 'url',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id,
          debug: 'Generic URL route'
        };
      }
    } else {
      // Regular chat message - RAG query
      console.log('Regular chat message detected - routing to CHAT');
      routeType = 'chat';
      data = {
        routeType: 'chat',
        message: text,
        query: text,
        text: text,
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        timestamp: new Date().toISOString(),
        debug: 'Chat message route - should go to RAG processing'
      };
    }
  }
  else {
    console.log('Message type not recognized');
    routeType = 'unknown';
    data = {
      routeType: 'unknown',
      debug: 'Message type not recognized',
      messageData: update.message
    };
  }
} else {
  console.log('No message found in update');
  routeType = 'unknown';
  data = {
    routeType: 'unknown',
    debug: 'No message found in update',
    updateData: update
  };
}

console.log('FINAL ROUTE DECISION:', routeType);
console.log('FINAL DATA:', JSON.stringify(data, null, 2));

return {
  json: data
};

// Debug Switch Input
const input = $json;
console.log('=== SWITCH INPUT DEBUG ===');
console.log('Input to switch:', JSON.stringify(input, null, 2));
console.log('routeType value:', input.routeType);
console.log('routeType type:', typeof input.routeType);

// Pass through the data unchanged
return { json: input };

// Debug Document Route
const input = $json;
console.log('=== DOCUMENT ROUTE DEBUG ===');
console.log('Reached document route with data:', JSON.stringify(input, null, 2));
return { json: input };

// Debug YouTube Route
const input = $json;
console.log('=== YOUTUBE ROUTE DEBUG ===');
console.log('Reached YouTube route with data:', JSON.stringify(input, null, 2));
return { json: input };

// Debug Chat Route
const input = $json;
console.log('=== CHAT ROUTE DEBUG ===');
console.log('Reached CHAT route with data:', JSON.stringify(input, null, 2));

// Send immediate response to confirm chat route is working
const response = {
  chatId: input.chatId,
  userId: input.userId,
  message: input.message || input.text,
  debug: 'SUCCESS: Chat route is working correctly!',
  routeType: input.routeType
};

console.log('Chat route processing successful:', response);
return { json: response };

