// Enhanced Route Detection with Bot Message Filtering
const update = $json;
let routeType = 'unknown';
let data = {};

// IMPORTANT: Skip bot messages to prevent loops
if (update.message && update.message.from && update.message.from.is_bot) {
  console.log('Skipping bot message to prevent loop');
  return {
    json: {
      routeType: 'skip',
      reason: 'Bot message ignored to prevent loop'
    }
  };
}

// Handle Telegram messages
if (update.message) {
  // Check if it's a command
  if (update.message.text?.startsWith('/')) {
    const command = update.message.text.split(' ')[0].toLowerCase();

    if (command === '/stats') {
      routeType = 'stats';
      data = {
        routeType: 'stats',
        command: 'stats',
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id
      };
    } else if (command === '/help') {
      routeType = 'help';
      data = {
        routeType: 'help',
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id
      };
    }
  }
  // Check if it's a document upload
  else if (update.message.document) {
    const doc = update.message.document;
    routeType = 'document';
    data = {
      routeType: 'document',
      fileId: doc.file_id,
      fileName: doc.file_name || 'unknown',
      mimeType: doc.mime_type || 'application/octet-stream',
      fileSize: doc.file_size,
      chatId: update.message.chat.id,
      userId: update.message.from.id,
      username: update.message.from.username || update.message.from.first_name,
      messageId: update.message.message_id,
      source: 'telegram'
    };
  } 
  // Check if it's a text message with URL
  else if (update.message.text) {
    const text = update.message.text;
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const urls = text.match(urlRegex);

    if (urls && urls.length > 0) {
      const url = urls[0];

      if (url.includes('youtube.com') || url.includes('youtu.be')) {
        routeType = 'youtube';
        data = {
          routeType: 'youtube',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id
        };
      } else if (url.includes('tiktok.com')) {
        routeType = 'tiktok';
        data = {
          routeType: 'tiktok',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id
        };
      } else {
        routeType = 'url';
        data = {
          routeType: 'url',
          url: url,
          chatId: update.message.chat.id,
          userId: update.message.from.id,
          username: update.message.from.username || update.message.from.first_name,
          messageId: update.message.message_id
        };
      }
    } else {
      // Regular chat message
      routeType = 'chat';
      data = {
        routeType: 'chat',
        message: text,
        query: text,
        text: text,
        chatId: update.message.chat.id,
        userId: update.message.from.id,
        username: update.message.from.username || update.message.from.first_name,
        messageId: update.message.message_id,
        timestamp: new Date().toISOString()
      };
    }
  }
}

return {
  json: data
};

// Merge extracted content with original data
const originalData = $('Detect Route Type').item.json;
const extractedText = $json.text || $json.transcript || $json.content || '';

return {
  json: {
    ...originalData,
    extractedText: extractedText,
    hasContent: extractedText.length > 0
  }
};

