// Prepare message for AI processing
const input = $json;
const userMessage = input.message || input.query || input.text || '';

if (!userMessage) {
  throw new Error('No message provided');
}

return {
  json: {
    userMessage: userMessage,
    chatId: input.chatId,
    userId: input.userId,
    username: input.username,
    messageId: input.messageId,
    timestamp: input.timestamp || new Date().toISOString()
  }
};

// Build comprehensive context from chat history and vector search results
let chatData, history = [], searchResults = [];

// Get chat data
try {
  chatData = $('Prepare Chat Input').item.json;
} catch (e) {
  throw new Error('Chat input data not available');
}

// Get chat history
try {
  history = $('Get Chat History').all() || [];
} catch (e) {
  console.log('No chat history available');
}

// Get vector search results
try {
  searchResults = $('Vector Search Documents').all() || [];
} catch (e) {
  console.log('No search results available');
}

// Format chat history
const formattedHistory = history
  .slice(0, 5)
  .reverse()
  .map(msg => `${msg.json.role === 'user' ? 'User' : 'Assistant'}: ${msg.json.content}`)
  .join('\n\n');

// Format search results with similarity scores
let relevantDocs = 'No relevant documents found.';
if (searchResults.length > 0) {
  relevantDocs = searchResults
    .filter(doc => doc.json && doc.json.similarity > 0.7)
    .slice(0, 3)
    .map(doc => {
      const data = doc.json;
      const title = data.title || 'Untitled';
      const classification = data.classification || 'Unknown';
      const content = (data.content || '').substring(0, 800);
      const filename = data.filename || 'Unknown file';
      const similarity = ((data.similarity || 0) * 100).toFixed(1);
      const dcwfTasks = data.dcwf_task_ids ? data.dcwf_task_ids.join(', ') : 'None';
      return `üìÑ **Document:** ${title}\nüìÅ **File:** ${filename}\nüè∑Ô∏è **Classification:** ${classification}\nüéØ **DCWF Tasks:** ${dcwfTasks}\nüìä **Similarity:** ${similarity}%\nüìù **Content:** ${content}...\n`;
    })
    .join('\n---\n');
}

// Build comprehensive system message
const systemMessage = `You are an AI Horizon assistant specializing in cybersecurity workforce analysis. You help analyze how artificial intelligence impacts cybersecurity tasks and roles based on the DCWF (Department of Homeland Security Cybersecurity Workforce Framework).

Your expertise includes:
- Classifying AI impact as: Replace, Augment, Remain Human, or New Task
- Mapping findings to specific DCWF task IDs and work roles
- Providing insights on workforce transformation
- Analyzing documents, videos, and research

Relevant documents from the knowledge base (with similarity scores):
${relevantDocs}

Recent conversation history:
${formattedHistory || 'No previous messages.'}

When relevant documents are found, reference them in your response with their similarity scores and DCWF task mappings. Be specific about the sources and provide actionable insights. Always mention specific DCWF task IDs when relevant.`;

return {
  json: {
    systemMessage: systemMessage,
    userMessage: chatData.userMessage,
    chatId: chatData.chatId,
    userId: chatData.userId,
    username: chatData.username,
    messageId: chatData.messageId,
    documentsFound: searchResults.length,
    historyCount: history.length,
    hasRelevantDocs: searchResults.filter(doc => doc.json && doc.json.similarity > 0.7).length > 0
  }
};

